use

const ReplyButton = ({
  post,
  handleAddComment,
  buttonText = "Reply", // Default button text
}) => {
  const [comment, setComment] = useState('');

  const handleClick = () => {
    if (comment.trim()) {
      handleAddComment(post.id, comment);
      setComment(''); // Clear the input field after adding the comment
    }
  };

  return (
    <button
      type="button"
      onClick={handleClick}
      className="bg-blue-400 hover:bg-blue-500 mt-5 text-white font-bold py-2 px-8 rounded-full mr-8 float-right"
    >
      {buttonText}
    </button>
  );
};

export default ReplyButton;
