import React from "react";

const ReplyButton = ({
  post,
  handleAddComment,
  buttonText = "Reply", // Default button text
}) => {
    const [commentText, setCommentText] = useState("");

    const handleClick = () => {
      if (commentText.trim()) {
        handleAddComment(post.id, commentText); // Menambahkan komentar
        setCommentText(""); // Menghapus input setelah komentar ditambahkan
      }
    };
    
    const handleInputChange = (event) => {
        setCommentText(event.target.value); // Memperbarui nilai state input
      };

  return (
    <button
      type="button"
      onClick={handleClick}
      className="bg-blue-400 hover:bg-blue-500 mt-5 text-white font-bold py-2 px-8 rounded-full mr-8 float-right"
    >
      {buttonText}
    </button>
  );
};

export default ReplyButton;
