import React, { useState, useRef } from "react";

const ReplyButton = ({
  post,
  handleAddComment,
  buttonText = "Reply", // Default button text
}) => {
  // State untuk menyimpan nilai input
  const [commentText, setCommentText] = useState("");

  // Referensi untuk input
  const inputRef = useRef(null);

  const handleClick = () => {
    if (commentText.trim()) {
      // Menambahkan komentar
      handleAddComment(post.id, commentText);
      
      // Mengosongkan input setelah komentar ditambahkan
      setCommentText("");
      
      // Mengembalikan fokus ke input setelah komentar ditambahkan
      if (inputRef.current) {
        inputRef.current.focus();
      }
    }
  };

  const handleInputChange = (event) => {
    setCommentText(event.target.value); // Memperbarui nilai state input
  };

  return (
    <div>
      {/* Input untuk menulis komentar */}
      <input
        type="text"
        id={`comment-input-${post.id}`}
        value={commentText}
        onChange={handleInputChange} // Mengupdate nilai input saat diubah
        ref={inputRef} // Menghubungkan input ke referensi
        className="border border-gray-300 p-2 rounded-md"
        placeholder="Write a comment..."
      />

      {/* Tombol Reply */}
      <button
        type="button"
        onClick={handleClick}
        className="bg-blue-400 hover:bg-blue-500 mt-5 text-white font-bold py-2 px-8 rounded-full mr-8 float-right"
      >
        {buttonText}
      </button>
    </div>
  );
};

export default ReplyButton;
